  plugins.push(new UglifyJsPlugin({ minimize: true }));
  outputFile = 'bundle.min.js';
} else {
  outputFile = 'bundle.js';
}

var config = {
......
  output: {
    path: path.resolve(__dirname, 'dist'),
    filename: outputFile
